package com.example.xxxxxx.phpwithrecyclerview;

import android.support.v7.app.AppCompatActivity;
import android.os.Bundle;
import android.support.v7.widget.CardView;
import android.support.v7.widget.LinearLayoutManager;
import android.support.v7.widget.RecyclerView;

import java.util.ArrayList;

public class MainActivity extends AppCompatActivity {

    RecyclerView recyclerView;
    RecyclerView.LayoutManager layoutManager;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        recyclerView = findViewById(R.id.recycler_view);

        recyclerView.setHasFixedSize(true);
        layoutManager=new LinearLayoutManager(this);
        recyclerView.setLayoutManager(layoutManager);

        ArrayList<item> itemArrayList = generateDummy();

        ItemAdapter itemAdapter = new ItemAdapter(this,itemArrayList);
        recyclerView.setAdapter(itemAdapter);

    }

    private ArrayList<item> generateDummy(){
        ArrayList<item> list = new ArrayList<>();
        for (int i=0;i<50;i++){
            if (i % 3 ==0){
                item items = new item();
                items.id = i;
                items.text = "Ronaldo"+i;
                items.img = "https://iasbh.tmgrup.com.tr/f2b7c5/752/395/0/32/800/452?u=https://isbh.tmgrup.com.tr/sbh/2018/05/27/cristiano-ronaldodan-ayrilik-sinyali-1527372887254.jpg";
                list.add(items);
            }
            else if (i % 7 == 0){
                item items = new item();
                items.id = i;
                items.text = "Flower"+i;
                items.img = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRLLDRm_o9KQcghNAJJ3UVzJafgKFVv6DzvhJukbg-Ccdf28n9RuA";
                list.add(items);
            }
            else if (i % 11 == 0){
                item items = new item();
                items.id = i;
                items.text = "Angkor Wat"+i;
                items.img = "data:image/jpeg;base64,";
                list.add(items);
            }
            else{
                item items = new item();
                items.id = i;
                items.text = "Dummy text"+i;
                items.img = "https://iasbh.tmgrup.com.tr/f2b7c5/752/395/0/32/800/452?u=https://isbh.tmgrup.com.tr/sbh/2018/05/27/cristiano-ronaldodan-ayrilik-sinyali-1527372887254.jpg";
                list.add(items);
            }

        }
        return list;
    }
}
